CREATE TABLE IF NOT EXISTS `actor` (
  `id` char(40) NOT NULL COMMENT '艺人ID，40位唯一标识符',
  `name` varchar(10) NOT NULL COMMENT '艺人姓名',
  `sex` char(1) DEFAULT NULL COMMENT '艺人性别',
  `birthday` date DEFAULT NULL COMMENT '艺人生日',
  `hometown` varchar(40) DEFAULT NULL COMMENT '艺人家乡',
  `introduction` varchar(200) DEFAULT NULL COMMENT '生涯介绍',
  `photo` varchar(128) DEFAULT NULL COMMENT '图片',
  `video_url` varchar(128) DEFAULT NULL COMMENT '视频链接',
  `click_like` int(11) DEFAULT NULL COMMENT '点赞数',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

-- --------------------------------------------------------

--
-- 表的结构 `ad`
--

CREATE TABLE IF NOT EXISTS `ad` (
  `id` char(40) NOT NULL COMMENT '广告业务ID，40位唯一标识符',
  `userId` char(40) DEFAULT NULL COMMENT '客户ID，40位唯一标识符',
  `content` varchar(128) DEFAULT NULL COMMENT '广告内容',
  `start_time` datetime DEFAULT NULL COMMENT '开始时间',
  `end_time` datetime DEFAULT NULL COMMENT '结束时间',
  `duration` int(11) DEFAULT NULL COMMENT '总时长',
  PRIMARY KEY (`id`),
  KEY `FK_AD_AD_USER_USER` (`userId`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

-- --------------------------------------------------------

