  `email` varchar(32) DEFAULT NULL,
  `telephone` varchar(16) DEFAULT NULL,
  `state` char(4) DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

