--
-- 表的结构 `countyeconomic`
--

CREATE TABLE IF NOT EXISTS `countyeconomic` (
  `id` char(40) NOT NULL COMMENT '使用函数创建唯一40位ID',
  `GRP` float unsigned DEFAULT NULL COMMENT '地区生产总值，单位亿元',
  `socialConsumerTotal` float unsigned DEFAULT NULL COMMENT '社会消费品零售总额，单位亿元',
  `area` float unsigned DEFAULT NULL COMMENT '面积，单位平方公里',
  `townNum` int(10) unsigned DEFAULT NULL COMMENT '乡镇数量',
  `villageNum` int(10) unsigned DEFAULT NULL COMMENT '行政村数量',
  `permanentPopulation` float unsigned DEFAULT NULL COMMENT '常住人口，单位万人',
  `urbanPopulation` float unsigned DEFAULT NULL COMMENT '城镇人口，单位万人',
  `ruralPopulation` float unsigned DEFAULT NULL COMMENT '农村人口，单位万人',
  `disposableIncome` float unsigned DEFAULT NULL COMMENT '居民人均可支配收入，单位元',
  `urbanDisposableIncome` float unsigned DEFAULT NULL COMMENT '城镇居民人均可支配收入，单位元',
  `ruralDisposableIncome` float unsigned DEFAULT NULL COMMENT '农村居民人均可支配收入，单位元',
  `ruralRoadMileage` float unsigned DEFAULT NULL COMMENT '农村公路里程数',
  `telUser` float unsigned DEFAULT NULL COMMENT '固定电话年末用户，单位万户',
  `mobileUser` float unsigned DEFAULT NULL COMMENT '移动电话年末用户，单位万人',
  `34GUser` float unsigned DEFAULT NULL COMMENT '3G4G移动电话用户，单位万户',
  `internetAccess` float unsigned DEFAULT NULL COMMENT '互联网宽带接入用户，单位万户',
  `individualHousehold` bigint(20) unsigned DEFAULT NULL COMMENT '个体工商户数，单位家',
  `registeredCompany` bigint(20) unsigned DEFAULT NULL COMMENT '注册企业数量（企业法人单位数）',
  `onlineStore` bigint(20) unsigned DEFAULT NULL COMMENT '网店数量，单位个',
  `mobileStore` bigint(20) unsigned DEFAULT NULL COMMENT '手机网店，微店数量，单位个',
  `ecTurnover` float unsigned DEFAULT NULL COMMENT '电子商务交易额，单位万元',
  `netRetailSales` float unsigned DEFAULT NULL COMMENT '网络零售额，单位万元',
  `year` year(2) DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8 COMMENT='县域经济发展情况（年度）';

