INSERT INTO `dict` (`id`, `dictCode`, `dictName`, `intro`, `state`) VALUES
('1', 'DICT_STATE', '状态', '0 禁用 1 可用', '1'),
('2', 'DICT_SEX', '性别', '0表示男；1表示女', '1'),
