--

CREATE TABLE IF NOT EXISTS `ecinfo` (
  `id` char(40) NOT NULL COMMENT '使用函数创建唯一40位ID',
  `category` tinyint(3) unsigned NOT NULL COMMENT '类别，调用字典',
  `title` varchar(16) NOT NULL COMMENT '标题',
  `author` varchar(16) NOT NULL COMMENT '作者',
  `published` datetime DEFAULT NULL COMMENT '发布时间',
  `content` text COMMENT '正文内容',
  `attachUrl` varchar(64) DEFAULT NULL COMMENT '附近路径',
  `keyword` varchar(32) DEFAULT NULL COMMENT '关键词',
