--
-- 表的结构 `ectrain`
--

CREATE TABLE IF NOT EXISTS `ectrain` (
  `id` char(40) NOT NULL COMMENT '使用函数创建唯一40位ID',
  `name` varchar(16) NOT NULL COMMENT '培训名',
  `category` tinyint(3) unsigned NOT NULL COMMENT '培训分类，字典',
  `content` text COMMENT '内容',
  `dayNum` int(10) unsigned NOT NULL COMMENT '天数',
  `period` tinyint(3) unsigned NOT NULL COMMENT '期数，字典',
  `peopleNum` int(10) unsigned NOT NULL COMMENT '人数',
  `target` tinyint(3) unsigned NOT NULL COMMENT '培训对象，针对人群',
  `published` datetime NOT NULL COMMENT '发布时间',
  `publisher` varchar(10) DEFAULT NULL COMMENT '发布人',
  PRIMARY KEY (`id`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8 COMMENT='电商培训';

