CREATE TABLE IF NOT EXISTS `role` (
  `id` varchar(32) NOT NULL COMMENT '40位唯一标识符',
  `name` varchar(32) DEFAULT NULL COMMENT '角色名',
  `state` char(4) DEFAULT NULL COMMENT '权限内容',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

